import { Link } from "react-router-dom";
import RoleNav from "./RoleNav";
import logo from "../images/e_logo.png";

const Header = () => {
  // Define inline styles as JavaScript objects
  const headerStyles = {
    backgroundColor: '#222222',
    padding: '15px 30px',
  };

  const brandStyles = {
    fontFamily: 'Arial, sans-serif',
    fontSize: '1.4rem',
    color: '#fff',
    textDecoration: 'none',
  };

  const brandHoverStyles = {
    color: '#f8b400',
    textDecoration: 'none',
  };

  const navLinkStyles = {
    color: '#fff',
    padding: '10px 20px',
    fontSize: '1rem',
    textDecoration: 'none',
  };

  const navLinkHoverStyles = {
    backgroundColor: '#f8b400',
    color: '#222222',
  };

  const navbarTogglerStyles = {
    border: 'none',
    backgroundColor: 'transparent',
    color: '#fff',
  };

  const navbarTogglerIconStyles = {
    backgroundColor: '#f8b400',
  };

  // Mobile and tablet styles (custom media queries using inline styles aren't possible directly, but we can handle it with conditional rendering or class names)
  return (
    <div>
      <nav style={headerStyles} className="navbar navbar-expand-lg text-color">
        <div className="container-fluid text-color">
          <img
            src={logo}
            height="60"
            width="auto"
            className="d-inline-block align-top"
            alt="Logo"
          />
          <Link to="/" style={brandStyles} className="navbar-brand">
            <i>
              <b style={{ color: '#f8b400' }} className="ms-2">Tours & Travel System</b>
            </i>
          </Link>

          <button
            style={navbarTogglerStyles}
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span style={navbarTogglerIconStyles} className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <RoleNav />
          </div>
        </div>
      </nav>

      {/* Mobile-responsive Styles */}
      <style>
        {`
          @media (max-width: 992px) {
            .navbar {
              padding: 10px 20px;
            }
            .navbar-brand {
              font-size: 1.2rem;
            }
            .navbar-toggler-icon {
              background-color: #f8b400;
            }
          }

          @media (max-width: 576px) {
            .navbar-brand {
              font-size: 1rem;
            }
            .navbar-nav {
              width: 100%;
              text-align: center;
              margin-top: 10px;
            }
            .nav-link {
              font-size: 0.9rem;
              padding: 8px 10px;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Header;
